from . models import Template, Link, Category, Image, Post

import random

#  from reactbackend import setupsomemodels as s
def clear_all():
    models = [Post, Template, Link, Category, Image]
    for m in models:
        for o in m.objects.all():
            o.delete()
        print(m.objects.all())

def setup_images():
    from django.contrib.staticfiles.utils import get_files
    from django.contrib.staticfiles.storage import StaticFilesStorage

    s = StaticFilesStorage()
    images = list(get_files(s, location='images'))
    for image in images:
        if Image.objects.filter(src=image).exists():
            pass
        else:
            img = Image.objects.create(title="titel", src=image, alt="alt", slideTitle="Slidetitel", slideText="Slidetext")



def setup_post():
    y=0
    while y < 5:
        titles = ['Unsere Hamster', 'Haben Sie gewusst...?', 'Meinst du auch...?', 'Wie kommt es...?', 'Bunte Blumen', "Chilli-Cola", "Brandneue Rauchmelder", "Fleischverbot im Schlachthof", "Bäume würden die fressen, wenn sie könnten!", "Nilgänse machen mobil gegen Liegewiesen"]
        title = random.choice(titles)
        subtitles = ['dass niemensch Klugshizzlers mag', 'dass Untertitel hübsch sind', 'für eine nachhaltiges Buzzword', 'über Milchprodukte aus patzigen Pflanzen', 'Fischers Franz grüßt Fischers Fritz']
        subtitle = random.choice(subtitles)

        template = Template.objects.order_by("?")[0]

        text = get_some_text()
        text = text[0:1500]
        extraText = text[0:75]

        post = Post.objects.create(
            category=Category.objects.last(),
            order=1,
            linkName="link"+str(y) + str(random.randint(0,600)),
            template=template,
            title=title,
            subTitle=subtitle,
            text="Template: <span class='text-info'>" + template.key + "</span><br> <br>" +text,
            extraText=extraText,
            mainImage=Image.objects.first(),
            
        )


        allimgs = Image.objects.all()   
        max_img = len(allimgs)
        how_many = random.randint(0, max_img-1)

        post.images.clear()
        x=0
        while x < how_many:
            which = random.randint(0, max_img-1)
            post.images.add(allimgs[which])
            x+=1
        post.links.set(Link.objects.all())
        post.save()
        y+=1


def setup_templates_and_cat():
    templates = [1,2,3,4]
    for t in templates:
        key = "template"+str(t)
        temp = Template.objects.create(key=key, name=key)

    name ="mehr über Enten"
    href = "https://de.wikipedia.org/wiki/Entenv%C3%B6gel"
    link = Link.objects.create(name=name, href=href)
    name ="Lorem ipsum?"
    href = "https://loremipsum.de/"
    link = Link.objects.create(name=name, href=href)

    category = Category.objects.create(name="Kategorie 1", linkName="Kategorie 1", order=2)

 
    
def setup():
    setup_images()
    setup_templates_and_cat()
    setup_post()






